import React, { useState } from 'react';
import { Menu, X } from 'lucide-react';

const Navigation = ({ activeSection, scrollToSection }) => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  return (
    <nav className="bg-slate-900/95 text-white py-4 px-6 fixed w-full z-50">
      <div className="max-w-9xl mx-auto flex justify-between items-center">
        <div className="text-xl font-bold">Portfolio</div>

        {/* Mobile Menu Button */}
        <button 
          className="lg:hidden"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>

        {/* Desktop Navigation */}
        <div className="hidden lg:flex space-x-6">
          {['home', 'about', 'projects', 'contact'].map((section) => (
            <button
              key={section}
              onClick={() => scrollToSection(section)}
              className={`hover:text-blue-400 transition-colors capitalize ${
                activeSection === section ? 'text-blue-400' : ''
              }`}
            >
              {section}
            </button>
          ))}
        </div>

        {/* Mobile Navigation */}
        {isMenuOpen && (
          <div className="absolute top-full left-0 right-0 bg-slate-900/95 lg:hidden">
            <div className="flex flex-col items-center py-4 space-y-4">
              {['home', 'about', 'projects', 'contact'].map((section) => (
                <button
                  key={section}
                  onClick={() => {
                    scrollToSection(section);
                    setIsMenuOpen(false);
                  }}
                  className={`hover:text-blue-400 transition-colors capitalize ${
                    activeSection === section ? 'text-blue-400' : ''
                  }`}
                >
                  {section}
                </button>
              ))}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navigation;
